import React, { useState } from 'react';

const Form = (handleAddExpense) => {
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [date, setDate] = useState("");

    const handleDescriptionAdd = (event) => {
        setDescription(event.target.value) 
    }

    const handlePriceAdd = (event) => {
        setPrice(event.target.value) 
    }

    const handleDateAdd = (event) => {
        setDate(event.target.value) 
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const newExpenseItem = {
            description,
            price,
            date};
            handleAddExpense(newExpenseItem);
        };

    return (
        <>
        <form clasname = "container" onSubmit={handleSubmit}>
        <input value={description} placeholder="description" type="text" onChange={handleDescriptionAdd} />
        <input value={price} placeholder="Price" type="number" onChange={handlePriceAdd}/>
        <input value={date} placeholder="Date" type="date" onChange={handleDateAdd}/>
        <button type="submit">Add to Expense List</button>
        </form>
        </>
    );
};

export default Form;
